calc.equation_logic_text:
var calc_output = [];

//define inputs
var organ_insufficiency = parseFloat(organ_insufficiency);
var acute_renal_failure = parseFloat(acute_renal_failure);
var a = parseFloat(age);
var b = parseFloat(temperature); //this always spits out celsius
var surgery = parseFloat(surgery);
var l = parseFloat(mean_arterial_pressure);
var c = parseFloat(ph);
var d = parseFloat(heart_rate);
var e = parseFloat(respiratory_rate);
var na = parseFloat(sodium);
var potassium = parseFloat(potassium);
var cr = parseFloat(creatinine);
var hct = parseFloat(hematocrit);
var wbc = parseFloat(white_blood_cell_count);
var k = parseFloat(glasgow_coma_scale);
var aa_grad = parseFloat(aa_grad);
var pao2 = parseFloat(pao2);
var fio2 = parseFloat(fio2);

//define result variables
var value = 0;
var msg = '';
var mini_msg = '';
var msg1 = '';
var msg2 = '';
var msg3 = '';

if (fio2 == 0) { //record PaO2 is FiO2 is <0.5
    value = pao2;
} else if (fio2 == 1) { //record Aa-gradient is FiO2 is â¥0.5
    value = aa_grad;
}

k = 15 - k; //GCS score = 15 - actual GCS
value = value + k; //add GCS score to total score 

if (wbc >= 40) { //white blood cell count
    value += 4;
} else if (20 <= wbc && wbc < 40) {
    value += 2;
} else if (15 <= wbc && wbc < 20) {
    value += 1;
} else if (3 <= wbc && wbc < 15) {
    value += 0;
} else if (1 <= wbc && wbc < 3) {
    value += 2;
} else if (wbc < 1) {
    value += 4;
}

if (hct >= 60) { //hematocrit (%)
    value += 4;
} else if (50 <= hct && hct < 60) {
    value += 2;
} else if (46 <= hct && hct < 50) {
    value += 1;
} else if (30 <= hct && hct < 46) {
    value += 0;
} else if (20 <= hct && hct < 30) {
    value += 2;
} else if (hct < 20) {
    value += 4;
}

if (UOMSYSTEM == false) { 
    cr = cr/88.4; //convert creatinine to US
}
if (UOMSYSTEM == true) {
    cr = cr;
}

if (acute_renal_failure == 1) { //if acute renal failure
    if (cr >= 3.5) {
        value += 8;
    } else if (2.0 <= cr && cr < 3.5) {
        value += 6;
    } else if (1.5 <= cr && cr < 2) {
        value += 4;
    } else if (0.6 <= cr && cr < 1.5) {
        value += 0;
    } else if (cr < 0.6) {
        value += 2;
    }
} else if (acute_renal_failure == 0) { //if no acute renal failure
    if (cr >= 3.5) {
        value += 4;
    } else if (2.0 <= cr && cr < 3.5) {
        value += 3;
    } else if (1.5 <= cr && cr < 2) {
        value += 2;
    } else if (0.6 <= cr && cr < 1.5) {
        value += 0;
    } else if (cr < 0.6) {
        value += 2;
    }
}

if (potassium >= 7) { //serum potassium
    value += 4;
} else if (6 <= potassium && potassium < 7) {
    value += 3;
} else if (5.5 <= potassium && potassium < 6) {
    value += 1;
} else if (3.5 <= potassium && potassium < 5.5) {
    value += 0;
} else if (3 <= potassium && potassium < 3.5) {
    value += 1;
} else if (2.5 <= potassium && potassium < 3) {
    value += 2;
} else if (potassium < 2.5) {
    value += 4;
}

if (na >= 180) { //serum sodium
    value += 4;
} else if (160 <= na && na < 180) {
    value += 3;
} else if (155 <= na && na < 160) {
    value += 2;
} else if (150 <= na && na < 155) {
    value += 1;
} else if (130 <= na && na < 150) {
    value += 0;
} else if (120 <= na && na < 130) {
    value += 2;
} else if (111 <= na && na < 120) {
    value += 3;
} else if (na < 111) {
    value += 4;
}

if (e >= 50) { //respiratory rate
    value += 4;
} else if (35 <= e && e < 50) {
    value += 3;
} else if (25 <= e && e < 35) {
    value += 1;
} else if (12 <= e && e < 25) {
    value += 0;
} else if (10 <= e && e < 12) {
    value += 1;
} else if (6 <= e && e < 10) {
    value += 2;
} else if (e < 6) {
    value += 4;
}

if (c >= 7.7) { //pH
    value += 4;
} else if (7.6 <= c && c < 7.7) {
    value += 3;
} else if (7.5 <= c && c < 7.6) {
    value += 1;
} else if (7.33 <= c && c < 7.5) {
    value += 0;
} else if (7.25 <= c && c < 7.33) {
    value += 2;
} else if (7.15 <= c && c < 7.25) {
    value += 3;
} else if (c < 7.15) {
    value += 4;
}

if (b >= 41) { //temp in celsius
    value += 4;
} else if (39 <= b && b < 41) {
    value += 3;
} else if (38.5 <= b && b < 39) {
    value += 1;
} else if (36 <= b && b < 38.5) {
    value += 0;
} else if (34 <= b && b < 36) {
    value += 1;
} else if (32 <= b && b < 34) {
    value += 2;
} else if (30 <= b && b < 32) {
    value += 3;
} else if (b < 30) {
    value += 4;
}

if (a <= 44) { //age
    value += 0;
} else if (44 < a && a <= 54) {
    value += 2;
} else if (54 < a && a <= 64) {
    value += 3;
} else if (64 < a && a <= 74) {
    value += 5;
} else if (a > 74) {
    value += 6;
}

if (organ_insufficiency == 0) { //history of organ failure = no
    value += 0;
} else if (organ_insufficiency == 1) { //history of organ failure = yes
    if (surgery == 0) {
        value += 5;
    } else if (surgery == 1) {
        value += 2;
    } else if (surgery == 2) {
        value += 5;
    }
}

if (d >= 180) { //heart rate
    value += 4;
} else if (140 <= d && d < 180) {
    value += 3;
} else if (110 <= d && d < 140) {
    value += 2;
} else if (70 <= d && d < 110) {
    value += 0;
} else if (55 <= d && d < 70) {
    value += 2;
} else if (40 <= d && d < 55) {
    value += 3;
} else if (d < 40) {
    value += 4;
}

if (l <= 49) { //mean arterial pressure
    value += 4;
} else if (l > 49 && l <= 69) {
    value += 2;
} else if (l > 69 && l <= 109) {
    value += 0;
} else if (l > 109 && l <= 129) {
    value += 2;
} else if (l > 129 && l <= 159) {
    value += 3;
} else if (l > 159) {
    value += 4;
}

value = value.toFixed(0); //round total score

if (organ_insufficiency == 1) {
    if (surgery == 0 || surgery == 1) {
        if (value >= 0 && value <= 4) {
            msg = '1';
        } else if (value >= 5 & value <= 9) {
            msg = '3';
        } else if (value >= 10 & value <= 14) {
            msg = '7';
        } else if (value >= 15 & value <= 19) {
            msg = '12';
        } else if (value >= 20 & value <= 24) {
            msg = '30';
        } else if (value >= 25 & value <= 29) {
            msg = '35';
        } else if (value >= 30 & value <= 34) {
            msg = '73';
        } else if (value > 34) {
            msg = '88';
        }
        msg1 = 'estimated postoperative mortality';
        mini_msg = 'Est. postoperative mortality';

    } else if (surgery == 2) {
        if (value >= 0 && value <= 4) {
            msg = '4';
        } else if (value >= 5 & value <= 9) {
            msg = '8';
        } else if (value >= 10 & value <= 14) {
            msg = '15';
        } else if (value >= 15 & value <= 19) {
            msg = '25';
        } else if (value >= 20 & value <= 24) {
            msg = '40';
        } else if (value >= 25 & value <= 29) {
            msg = '55';
        } else if (value >= 30 & value <= 34) {
            msg = '73';
        } else if (value > 34) {
            msg = '85';
        }
        msg1 = 'estimated nonoperative mortality';
        mini_msg = 'Est. nonoperative mortality';
    }
} else if (organ_insufficiency == 0) {
    if (value >= 0 && value <= 4) {
        msg = '1';
        msg2 = '4';
    } else if (value >= 5 & value <= 9) {
        msg = '3';
        msg2 = '8';
    } else if (value >= 10 & value <= 14) {
        msg = '7';
        msg2 = '15';
    } else if (value >= 15 & value <= 19) {
        msg = '12';
        msg2 = '25';
    } else if (value >= 20 & value <= 24) {
        msg = '30';
        msg2 = '40';
    } else if (value >= 25 & value <= 29) {
        msg = '35';
        msg2 = '55';
    } else if (value >= 30 & value <= 34) {
        msg = '73';
        msg2 = '73';
    } else if (value > 34) {
        msg = '88';
        msg2 = '85';
    }
    msg1 = 'estimated postoperative mortality';
    mini_msg = 'Est. postoperative mortality';
    msg3 = 'estimated nonoperative mortality';
}

calc_output.push({
    name: 'mini',
    value: msg,
    value_text: '%',
    message: mini_msg
});
if (organ_insufficiency == 1) {
calc_output.push({
    name: '1868_APACHE II Score_result',
    value: value,
    value_text: 'points',
    message: msg + '% ' + msg1  
});
}
if (organ_insufficiency == 0) {
calc_output.push({
    name: '1868_APACHE II Score_result',
    value: value,
    value_text: 'points',
    message: msg + '% ' + msg1 +'<br/>' + msg2 + '% ' + msg3 
});
}
