calc.equation_logic_text:
var calc_output = [];
systolic_bp = parseInt(systolic_bp);
heart_rate = parseInt(heart_rate);
respiratory_rate = parseInt(respiratory_rate);
temperature = parseInt(temperature);
avpu_score = parseInt(avpu_score);
var v = systolic_bp + heart_rate + respiratory_rate + temperature + avpu_score;
var msg = '';
var msgalt = '';
var mini_msg = '';
if (v == 0 || v == 1 || v == 2) {
    msg = '7.9% chance of ICU admission or death within 60 days.';
    mini_msg = '7.9% chance of ICU admission.'
} else if (v == 3 || v == 4) {
    msg = '12.7% chance of ICU admission or death within 60 days.';
    mini_msg = '12.7% chance of ICU admission.'
} else if (v >= 5) {
    msg = '30% chance of ICU admission or death within 60 days. Consider higher level of care.';
    mini_msg = '30% chance of ICU admission.'
}
if (systolic_bp == 3 || heart_rate == 3 || respiratory_rate == 3 || temperature == 3 || avpu_score == 3) {
    msgalt = 'For any single physiological parameter with assigned score of 3, consider higher level of care for patient.'
}
calc_output.push({
    name: 'mini',
    value: v.toFixed(0),
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: 'Modified Early Warning Score',
    value: v.toFixed(0),
    value_text: 'points',
    message: msg + '<br/><br/>' + msgalt
});
