calc.equation_logic_text:
var calc_output=[]; 
var msg; 
var u=parseFloat(pulse)+parseFloat(sweat)+parseFloat(restless)+parseFloat(pupil)+parseFloat(bone)+parseFloat(noise)+parseFloat(gi)+parseFloat(tremor)+parseFloat(yawn)+parseFloat(anxiety)+parseFloat(skin);

if(u<5){msg='No active withdrawal'}
else if(u>=5&&u<=12){msg='Mild withdrawal'}
else if(u>=13&&u<=24){msg='Moderate withdrawal'}
else if(u>=25&&u<=36){msg='Moderately severe withdrawal'}
else if(u>36){msg='Severe withdrawal'} 

calc_output.push({
    name:'mini',
    value:u.toFixed(0),
    value_text:'points',
    message:'COWS Score'
}); 
calc_output.push({
    name:'1985_COWS Score_result',
    value:u.toFixed(0),
    value_text:'points',
    message:msg
});
