var calc_output = [];

var male, attack, onset, red, mtp, cvd, uric;
male = parseFloat(male);
attack = parseFloat(attack);
onset = parseFloat(onset);
red = parseFloat(red);
mtp = parseFloat(mtp);
cvd = parseFloat(cvd);
uric = parseFloat(uric);

var u = male + attack + onset + red + mtp + cvd + uric;
var msg = '';
var mini_msg = '';
if (u <= 4) { mini_msg = '2.2% prevalence of gout in original study.'; msg='Gout unlikely and other diagnoses should be considered.'; }
if (u > 4 && u < 8) { mini_msg = '31.2% prevalence of gout'; msg='Scores from >4 and <8 are equivocal. These patients often benefit the most from synovial fluid analysis for crystals.'; }
if (u >= 8) { mini_msg = '82.5% prevalence of gout in original study.'; msg='Gout is very likely.'; }

calc_output.push({ name: 'mini', value: u, value_text: 'points', message: mini_msg });
calc_output.push({ name: 'Acute Gout Diagnosis Rule', value: u, value_text: 'points', message: mini_msg+'<br/><br/>'+msg });
