calc.equation_logic_text:
var calc_output = [];
var msg = '';
var os = '';
var u = parseFloat(age) + parseFloat(nodal) + parseFloat(ldh) + parseFloat(hgb) + parseFloat(stage);
var msg = '';
var mini_msg = '';
if (u <= 1) {
     msg = 'Low Risk';
     os = '10-year overall survival is approximately 70%';
}
if (u == 2) {
   msg = 'Intermediate Risk';
   os = '10-year overall survival is approximately 50%';
}
if (u >= 3) {
    msg = 'High Risk';
    os = '10-year overall survival is approximately 35%';
}

calc_output.push({ name: 'mini', value: u, value_text: 'points', message: msg });
calc_output.push({ name: 'FLIPI', value: u, value_text: 'points', message: msg+'<br/><br/>'+os });
