calc.equation_logic_text:
var calc_output = [];
var tumor = parseInt(tumor);
var nodal = parseInt(nodal);
var systemic = parseInt(systemic);
var spleen = parseInt(spleen);
var compress = parseInt(compress);
var pleural = parseInt(pleural);
var leuk = parseInt(leuk);
var cytopenia = parseInt(cytopenia);
var u = tumor + nodal + systemic + spleen + compress + pleural + leuk + cytopenia;
var msg;
var mini_msg;
if (u < 1) { mini_msg = 'Negative for GELF Criteria';
    msg = '<br/><br/>Immediate therapy is NOT indicated.';
} else {
    mini_msg = 'Positive for GELF Criteria';
    msg = '<br/><br/>Immediate therapy is indicated.<br/><br/>Note: When a patient has ≥1 criteria, this is considered “high” tumor burden.';
}

calc_output.push({ name: 'mini', value: u.toFixed(0), value_text: 'criteria', message: mini_msg });
calc_output.push({ name: 'GELF Criteria', value: u.toFixed(0), value_text: 'criteria', message: mini_msg+msg });
