var calc_output = [];
var units_res1, units_res2, u, v;


if (UOMSYSTEM == true) { // US units, so let'em through
  var atm = parseFloat(atmospheric_pressure);
  var paco2 = parseFloat(paco2);
  var pao2 = parseFloat(pao2);
  var units_res1 = "mm Hg";
}

if (UOMSYSTEM == false) { // convert these guys to US units
  var atm = parseFloat(atmospheric_pressure) / 0.133;
  var paco2 = parseFloat(paco2) / 0.133;
  var pao2 = parseFloat(pao2) / 0.133;
  var units_res1 = "kPa";
}

// now we're in US units. let's make some helper vars so the math doesn't screw us up

var fio2 = parseFloat(fio2) / 100; // turn into a % regardless of unit
var h2o = 47; // water vapor's partial pressure in mmHg, which is fine bc we've already converted to US units above
var atm_h2o = atm - h2o;
var paco2_r = paco2 / 0.8;
var v = ((fio2 * atm_h2o) - paco2_r) - pao2;

if (UOMSYSTEM == false) { // convert v back to kPa of course!
  v = v * 0.133;
}

// We can keep these without making unit swaps bc we defined units above!

calc_output.push({
  name: 'mini',
  value: v.toFixed(1),
  value_text: units_res1,
  message: 'A-a Gradient'
});
calc_output.push({
  name: '243_A-a Gradient_result (gradient)',
  value: v.toFixed(1),
  value_text: units_res1,
  message: 'A-a Gradient'
});


var age = parseFloat(age);

if (age == ' ' || age == 'null' || age == 'undefined' || age == 0 || age == '') {

} else {

  var u = 0;
  u = ((age / 4) + 4); // this is the expected a-a grad for age in mm Hg

  if (UOMSYSTEM == false) {
    u = u * 0.133;
  }
  calc_output.push({
    name: '243_A-a Gradient_result (expected)',
    value: u.toFixed(1),
    value_text: units_res1,
    message: 'Expected A-a Gradient for Age'
  });
}
