calc.equation_logic_text:
var calc_output = [];
var score = parseFloat(temp) +
    parseFloat(leuks) +
    parseFloat(trach) +
    parseFloat(oxy) +
    parseFloat(pulm) +
    parseFloat(culture);

var msg, mini_msg;

switch (true) {

    case (score <= 6):
        mini_msg = 'Use clinical suspicion for decision to culture or perform BAL.';
        msg = 'If the clinical suspicion for VAP is LOW, do NOT culture sputum. Evaluate for other potential sources of infection.<br/><br/>If the clinical suspicion for VAP is HIGH, perform bronchoalveolar lavage (BAL) or mini-BAL.';
        break;

    case (score > 6):
        mini_msg = 'Perform bronchoalveolar lavage (BAL)';
        msg = 'Perform bronchoalveolar lavage (BAL) or mini-BAL.';
        break;

    default:
        msg = "error"
        break;
}
calc_output.push({ name: 'mini', value: score.toFixed(0), value_text: 'points', message: mini_msg });
calc_output.push({ name: 'CPIS Score', value: score.toFixed(0), value_text: 'points', message: msg });
