calc.equation_logic_text:
var calc_output = [];

var ca = parseInt(ca);
var plt = parseInt(plt);
var hb = parseInt(hb);
var leuk = parseInt(leuk);
var bmi = parseInt(bmi);
var u = ca + plt + hb + leuk + bmi;
var msg;

if (u == 0) { mini_msg = 'Low Risk';
    msg = 'Low Risk (0 Points)<br/><br/>Rate of VTE at 2.5 months* = 0.3 - 0.8%'; }
if (u == 1 || u == 2) { mini_msg = 'Intermediate Risk';
    msg = 'Intermediate Risk (1-2 Points)<br/><br/>Rate of VTE at 2.5 months* = 1.8 - 2.0%'; }
if (u > 2) { mini_msg = 'High Risk';
    msg = 'High Risk (≥3 Points)<br/><br/>Rate of VTE at 2.5 months* = 6.7 - 7.1%'; }
msg += "<br/><br/>*Note: Median follow-up time was 2.5 months.";

calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: 'Khorana Risk Score',
    value: u.toFixed(0),
    value_text: 'points',
    message: msg
});
