var calc_output = [];
var u = parseFloat(dil) + parseFloat(eff) + parseFloat(stat) + parseFloat(pos) + parseFloat(con);
var msg, mini_msg;


if (u <= 5) { mini_msg='Suggests unfavorable cervix and induction may be necessary.'; msg = 'Scores ≤ 5 suggest an unfavorable cervix, and that induction may be necessary for successful vaginal delivery.'; }
if (u == 6 || u == 7) { mini_msg='Unable to determine success of induction and vaginal delivery.'; msg = 'Scores 6-7 do not definitively predict whether or not induction will be successful. Consider induction or augmentation of labor based on clinical judgment.'; }
if (u >= 8) { mini_msg='Suggests successful spontaneous vaginal delivery.'; msg = 'Scores ≥ 8 suggest spontaneous vaginal delivery is more likely and augmentation or induction may be unnecessary.'; }
msg += '<br/><br/>Note: Success rates tend to vary based on indication for induction and weeks gestation.';


calc_output.push({ name: 'mini', value: u.toFixed(0), value_text: 'points', message: mini_msg });
calc_output.push({ name: 'Bishop Score', value: u.toFixed(0), value_text: 'points', message: msg });
