calc.equation_logic_text:
var calc_output = [];

var wheezing = parseInt(wheezing);
var major = parseInt(asthma) + parseInt(eczema) + parseInt(airallerg);
var minor = parseInt(foodaller) + parseInt(cold) + parseInt(eos);
var msg;
var mini_msg;
var string;
var info = "See sensitivity and specificity information in the About section."

if (wheezing == 1) {
    if (major > 0) {
        string = "Positive";
        mini_msg = "Likely asthma diagnosis.";
        msg = ">98% specific for asthma diagnosis by age 11.";
    } else if (major == 0 && minor <= 1) {
        string = "Negative";
        mini_msg = "Less likely asthma diagnosis.";
        msg = "Patient less likely to develop asthma by age 11.";
    } else if (major == 0 && minor > 1) {
        string = "Positive";
        mini_msg = "Likely asthma diagnosis.";
        msg = ">98% specific for asthma diagnosis by age 11.";
    }
} else { string = "Negative";
    mini_msg = "Less likely asthma diagnosis.";
    msg = "Patient less likely to develop asthma by age 11." }

calc_output.push({ name: 'mini', value: string, value_text: ' ', message: mini_msg });
calc_output.push({ name: 'Modified Asthma Predictive Index', value: string, value_text: ' ', message: msg + '<br/><br/>' +info });
