var calc_output = [];
var v = parseInt(wheezing) + parseInt(air) + parseInt(scal) + parseInt(sup) + parseInt(o2);
var msg;

if (v < 4) {
    msg = 'Mild asthma';
}
if (v > 3 && v < 8) {
    msg = 'Moderate asthma';
}
if (v > 7) {
    msg = 'Severe asthma';
}

calc_output.push({ name: 'mini', value: v.toFixed(0), value_text: 'points', message: msg });
calc_output.push({ name: 'PRAM Score', value: v.toFixed(0), value_text: 'points', message: msg });
