calc.equation_logic_text:
var calc_output = [];
var u = parseFloat(face) + parseFloat(limb) + parseFloat(vent);
var msg, mini_msg;
if (u <= 3) {
    mini_msg = "No pain";
    msg = "";
}
if (u == 4 || u == 5) {
    mini_msg = "Mild pain";
    msg = "";
}
if (u >= 6 && u <= 11) {
    mini_msg = "Unacceptable amount of pain";
    msg = "<br/><br/>Consider further sedation or other analgesia.";
}
if (u >= 12) {
    mini_msg = "Maximum pain";
    msg = "<br/><br/>Consider further sedation or other analgesia.";
}

calc_output.push({ name: 'mini', value: u, value_text: 'points', message: mini_msg });
calc_output.push({ name: 'BPS', value: u, value_text: 'points', message: mini_msg+msg });
