calc.equation_logic_text:
var calc_output = [];

var u, msg, pcntm, pcntr, risk;
u =  parseFloat(age)+parseFloat(shock)+parseFloat(comor)+parseFloat(diag)+parseFloat(srh);

switch (u) {
case 0: pcntm = "0"; pcntr = "4.9"; risk="Low Risk"; break;
case 1: pcntm = "0"; pcntr = "3.4"; risk="Low Risk"; break;
case 2: pcntm = "0.2"; pcntr = "5.3"; risk="Intermediate Risk"; break;
case 3: pcntm = "2.9"; pcntr = "11.2"; risk="Intermediate Risk";break;
case 4: pcntm = "5.3"; pcntr = "14.1"; risk="Intermediate Risk";break;
case 5: pcntm = "10.8"; pcntr = "24.1"; risk="High Risk";break;
case 6: pcntm = "17.3"; pcntr = "32.9"; risk="High Risk"; break;
case 7: pcntm = "27"; pcntr = "43.8"; risk="High Risk"; break;
default: pcntm = "41.1"; pcntr = "41.8"; risk="High Risk"; break;
}

mini_msg = risk;
msg= "<br/><br/>" + pcntr + "% rebleeding risk.<br/><br/>"+ pcntm + "% mortality.<br/><br/>See all mortality rates under Evidence > Facts & Figures.";

calc_output.push({ name: 'mini', value: u, value_text: 'points', message: mini_msg });
calc_output.push({ name: 'Rockall Complete', value: u, value_text: 'points', message: mini_msg+msg });
