calc.equation_logic_text:
var calc_output = [];
var string = '';
var minimsg = '';

var u = parseFloat(consolability) + parseFloat(body) + parseFloat(face) +
  parseFloat(vocal) + parseFloat(breath);



if (u == 0) {
  minimsg = "No pain";
} else if (u >= 1 && u <= 3) {
  minimsg = "Mild pain";
} else if (u >= 4 && u <= 6) {
  minimsg = "Moderate pain";
} else if (u >= 7) {
  minimsg = "Severe pain";
}

string =
  'Ranges are based on a standard 0-10 scale of pain, but the original tool only quantifies 0 as "No pain" and 10 as "Severe pain".';


calc_output.push({
  name: 'mini',
  value: u.toFixed(0),
  value_text: 'points',
  message: minimsg
});
calc_output.push({
  name: 'APGAR Score',
  value: u.toFixed(0),
  value_text: 'points',
  message: minimsg + '<br/>' + string
});
