calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age);

var eye, verbal, motor

age == 0 ? eye = parseFloat(eyePed) : eye = parseFloat(eyeInfant)
age == 0 ? verbal = parseFloat(verbalPed) : verbal = parseFloat(verbalInfant)
age == 0 ? motor = parseFloat(motorPed) : motor = parseFloat(motorInfant)

// SCORE (total points)
var score = eye + motor + verbal

// SCALE (individual components)
var eye_scale, verbal_scale, motor_scale

eye == 0 ? eye_scale = 'NT' : eye_scale = eye

verbal == 0 ? verbal_scale = 'NT' : verbal_scale = verbal

motor == 0 ? motor_scale = 'NT' : motor_scale = motor

var scale = 'E(' + eye_scale + ') ' + 'V(' + verbal_scale + ') ' + 'M(' + motor_scale + ') ' 

var scale_msg, score_msg

age == 0 ? scale_msg = 'Glasgow Coma Scale' : scale_msg = 'Pediatric Glasgow Coma Scale'
age == 0 ? score_msg = 'Glasgow Coma Score' : score_msg = 'Pediatric Glasgow Coma Score'


if (eye == 0 || verbal == 0 || motor == 0) {
  calc_output.push({ 
    name: 'mini', 
    value: scale, 
    value_text: '', 
    message: scale_msg
  }); 
  calc_output.push({ 
    name: 'Glasgow Coma Score', 
    value: '', 
    value_text: '', 
    message: score_msg + ' cannot be calculated (one or more components not testable)'
  });
}
else {
  calc_output.push({ 
    name: 'mini', 
    value: score, 
    value_text: 'points', 
    message: score_msg 
  }); 
  calc_output.push({ 
    name: 'Glasgow Coma Score', 
    value: score, 
    value_text: 'points', 
    message: score_msg
  });
}
  calc_output.push({ 
    name: 'Result', 
    value: scale,
    value_text: '', 
    message: scale_msg
  });
