var calc_output = [];

var v = parseFloat(temp) + parseFloat(cns) + parseFloat(gihep) + parseFloat(tachy) + parseFloat(chf) + parseFloat(afib) + parseFloat(precip);

var msg;

if (v < 25) {
    msg = 'Unlikely to represent thyroid storm';
} else if (v >= 25 && v <= 44) {
    msg = 'Suggestive of impending thyroid storm';
} else if (v >= 45) {
    msg = 'Highly suggestive of thyroid storm';
}

calc_output.push({
  name: 'mini',
  value: v,
  value_text: 'points',
  message: msg
});
calc_output.push({
  name: '3816_Burch-Wartofsky_result',
  value: v,
  value_text: 'points',
  message: msg
});
