calc.equation_logic_text:
var cr = cr || 0


var calc_output = [];

var bun = parseFloat(bun);
var sbp = parseFloat(sbp);
cr = parseFloat(cr);

var risk = '';
var mortGroup = '';

if (bun == 0) {
    if (sbp == 0) {
        risk = 'Low Risk';
        mortGroup = '2.1-2.3% Mortality Risk from ADHF';
    }
    if (sbp == 1) {
        risk = 'Intermediate Risk';
        mortGroup = '5.5-13.2% Mortality Risk from ADHF';
    }
}

if (bun == 1) {
    risk = 'Intermediate Risk';
    mortGroup = '5.5-13.2% Mortality Risk from ADHF';
}


if (bun == 1 && cr == 1 && sbp == 1) {
    risk = 'High Risk';
    mortGroup = '19.8-21.9% Mortality Risk from ADHF';
}

calc_output.push({
    name: 'mini',
    value: '',
    value_text: '',
    message: risk
});
calc_output.push({
    name: 'adhere',
    value: risk,
    value_text: '',
    message: mortGroup
});
