calc.equation_logic_text:
var calc_output = [];
var u = parseInt(cvc) + parseInt(wbc) + parseInt(num_pic) + parseInt(vte_hx) + parseInt(ca);
var msg = 'Probability of VTE if PICC inserted';
var cl, prob, risk;
if (u == 0) {
    cl = "Class I";
    prob = "0.9";
    risk = "Low risk";
}
if (u == 1 || u == 2) {
    cl = "Class II";
    prob = "1.6";
    risk = "Intermediate risk";
}
if (u >= 3 && u <= 4) {
    cl = "Class III";
    prob = "2.7";
    risk = "High risk";
}
if (u > 4) {
    cl = "Class IV";
    prob = "4.7";
    risk = "High risk";
}
calc_output.push({ 
  name: 'mini', 
  value: cl, 
  value_text: '', 
  message: risk 
});
calc_output.push({ 
  name: 'Score', 
  value: u, 
  value_text: 'points', 
  message: '' 
});
calc_output.push({ 
  name: 'Michigan PICC Risk', 
  value: cl, 
  value_text: '', 
  message: risk
});
calc_output.push({ 
  name: 'Risk', 
  value: prob, 
  value_text: '%', 
  message: msg 
});
