calc.equation_logic_text:
var calc_output = [];

var micro = parseFloat(micro);
var alb = parseFloat(alb);
var ca = parseFloat(ca);
var ldh = parseFloat(ldh);

var iss_stage

if (micro == 0) {
	if (alb == 1) { // normal (high) albumin
		iss_stage = 1
	} else if (alb == 0) { // abnormal (low) albumin
		iss_stage = 2
	}
} else if (micro == 1) {
	iss_stage = 2
} else if (micro == 2) {
	iss_stage = 3
}

var riss;

if (iss_stage == 1 && (ca + ldh == 0)) {
	riss = 'R-ISS Stage I'
} else if (iss_stage == 1 && (ca + ldh > 0)) {
	riss = 'R-ISS Stage II'
} else if (iss_stage == 2) {
	riss = 'R-ISS Stage II'
} else if (iss_stage == 3 && (ca + ldh == 0)) {
	riss = 'R-ISS Stage II'
} else if (iss_stage == 3 && (ca + ldh > 0)) {
	riss = 'R-ISS Stage III'
}

var pfs

if (riss == 'R-ISS Stage I') {
	pfs = 66 
} 
if (riss == 'R-ISS Stage II') {
	pfs = 42
}
if (riss == 'R-ISS Stage III') {
	pfs = 29
}

calc_output.push({
    name: 'mini',
    value: riss,
    value_text: ' ',
    message: "Multiple Myeloma"
});
calc_output.push({
    name: 'R-ISS',
    value: riss,
    value_text: ' ',
    message: pfs + ' months median progression-free survival'
});
