var calc_output = [];
var abv, ebv, abl;
var msg = 'Allowable Blood Loss';
var age = parseFloat(age);
var hi = parseFloat(hi);
var hf = parseFloat(hf);
var wt = parseFloat(wt);
var hav = (hi+hf)/2;

if (age == 1) {
    abv = 75;
}
if (age == 2) {
    abv = 65;
}
if (age == 3) {
    abv = 80;
}
if (age == 4) {
    abv = 85;
}
if (age == 5) {
    abv = 96;
}
ebv = wt * abv;

abl = ((ebv * (hi - hf)) / hav);
calc_output.push({ name: 'mini', value: abl.toFixed(1), value_text: 'mL', message: 'Allowable Blood Loss' });
calc_output.push({ name: 'max ABL', value: abl.toFixed(1), value_text: 'mL', message: 'Allowable Blood Loss' });
