calc.equation_logic_text:
var calc_output = [];

var msg;
var sex = parseFloat(sex);
var age = parseFloat(age);
var hct = parseFloat(hct);
var esr = parseFloat(esr);
if (UOMSYSTEM == true) {
    var alb = parseFloat(alb);
} else {
    var alb = parseFloat(alb) / 10;
}
var ap = parseFloat(ap);
var stools = parseFloat(stools);
var func = parseFloat(func);
var wt = parseFloat(wt);
var ht = parseFloat(ht);
var abd = parseFloat(abd);
var peri = parseFloat(peri);
var intest = parseFloat(intest);
var hctscore, esrscore, albscore, score;

//hct
if (age <= 10) {
    if (hct > 33) { hctscore = 0 }
    if (hct >= 28 && hct <= 33) { hctscore = 2.5 }
    if (hct < 28) { hctscore = 5 }
}
if (age >= 11 && age <= 14 && sex == 0) {
    if (hct >= 35) { hctscore = 0 }
    if (hct >= 30 && hct < 35) { hctscore = 2.5 }
    if (hct < 30) { hctscore = 5 }
}
if (age >= 11 && age <= 19 && sex == 1) {
    if (hct >= 34) { hctscore = 0 }
    if (hct >= 29 && hct < 34) { hctscore = 2.5 }
    if (hct < 29) { hctscore = 5 }
}
if (age > 14 && age <= 19 && sex == 0) {
    if (hct >= 37) { hctscore = 0 }
    if (hct >= 32 && hct < 37) { hctscore = 2.5 }
    if (hct < 32) { hctscore = 5 }
}
// if (age > 19) { msg = 'The PCDAI does not apply to patients >19 years old.';
//     score = 'N/A' }

//esr
if (esr < 20) { esrscore = 0 }
if (esr >= 20 && esr <= 50) { esrscore = 2.5 }
if (esr > 50) { esrscore = 5 }

//alb
if (alb >= 3.5) { albscore = 0 }
if (alb >= 3.1 && alb < 3.5) { albscore = 5 }
if (alb < 3.1) { albscore = 10 }

var score = ap + stools + func + wt + ht + abd + peri + intest + hctscore + esrscore + albscore;

if (score < 10) { msg = "Remission"; }
if (score >= 10 && score <= 27.5) { msg = "Mild Crohn's Disease Activity"; }
if (score > 27.5 && score <= 40) { msg = "Moderate Crohn's Disease Activity"; }
if (score >= 40) { msg = "Severe Crohn's Disease Activity"; }

calc_output.push({
    name: 'mini',
    value: score.toFixed(1),
    value_text: 'points',
    message: msg
});
calc_output.push({
    name: 'pcdai',
    value: score.toFixed(1),
    value_text: 'points',
    message: msg
});
