var calc_output = [];

var u = parseFloat(gram) + parseFloat(prot) + parseFloat(blood_anc) + parseFloat(seizures) + parseFloat(spinal_neut);
var msg;

if (u == 0) { msg = "Very low risk for bacterial meningitis."; }
if (u > 0) { msg = "NOT very low risk for bacterial meningitis."; }

calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: msg
});
calc_output.push({
    name: 'bac meningitis',
    value: u,
    value_text: 'points',
    message: msg
});
