var calc_output = [];
var u, msg, mini_msg;


u = parseFloat(stairs) + parseFloat(mobile) + parseFloat(transfer) + parseFloat(toilet) + parseFloat(bowel) + parseFloat(bladder) + parseFloat(dress) + parseFloat(groom) + parseFloat(bath) + parseFloat(feed);




if (u < 20) {
    mini_msg = "Totally dependent";
    msg = "Poor outcome predicted";
} else if (u >= 20 && u <= 39) {
    mini_msg = "Very dependent";
    msg = "Poor outcome predicted";
} else if (u >= 40 && u <= 59) {
    mini_msg = "Partially dependent";
    msg = "Intermediate outcome predicted";
} else if (u >= 60 && u <= 79) {
    mini_msg = "Minimally dependent";
    msg = "Good outcome predicted";
} else if (u >= 80) {
    mini_msg = "Totally independent";
    msg = "Good outcome predicted";
}

calc_output.push({ 
    name: 'mini', 
    value: u, 
    value_text: 'points', 
    message: mini_msg 
});
calc_output.push({ 
    name: '3912_Barthel Index_result', 
    value: u, 
    value_text: 'points', 
    message: mini_msg
});
