var calc_output = [];
var u = parseFloat(vte) + parseFloat(known) + parseFloat(paralysis) + parseFloat(ca) + parseFloat(immob) + parseFloat(icu) + parseFloat(age);
var msg = '';
var mini_msg = '';
if (u <= 2) {
    msg = 'Low risk of venous thromboembolism (VTE).';
    mini_msg = 'Low risk'
}
if (u >= 3) {
    msg = 'High risk of venous thromboembolism (VTE).';
    mini_msg = 'High risk'
}
calc_output.push({ name: 'mini', value: u, value_text: 'points', message: mini_msg });
calc_output.push({ name: 'IMPROVE RAM', value: u, value_text: 'points', message: msg });
