calc.equation_logic_text:
var calc_output = [];

//initialize user inputs
var age = parseFloat(age);
var aarbor_stage = parseFloat(aarbor_stage);
var ecog = parseFloat(ecog);
var serum = parseFloat(serum);
var nodal = parseFloat(nodal);

//score from Standard IPI formula
var u = age + aarbor_stage + ecog + serum + nodal;

//outputs
var pfs, os, group;

//assign values for Standard IPI
if (u == 0 || u == 1){
		pfs = '85';
		os = '82';
		group = 'Low risk group';
}else if(u == 2){
		pfs = '80';
		os = '81';
		group = 'Low-intermediate risk group';
}else if(u == 3){
	pfs = '57';
	os = '49';
	group = 'High-intermediate risk group';
}else{
	pfs = '51';
	os = '59';
	group = 'High risk group';
}


//assign values for Revised IPI
var rpfs, ros, rgroup;
if (u == 0){
	rpfs = '94';
	ros = '94';
	rgroup = 'Very good prognosis';
}else if(u == 1 || u == 2){
	rpfs = '80';
	ros = '79';
	rgroup = 'Good prognosis';
}else{
	rpfs = '53';
	ros = '55';
	rgroup = 'Poor prognosis';
}

//output displayed
calc_output.push({
	name: 'mini',
	value: u,
	value_text: 'points',
	message: rgroup
});

calc_output.push({
	name: 'Score',
	value: u,
	value_text: 'points',
	message: rgroup + ' (R-IPI)<br/>' + group + ' (IPI)'
});

calc_output.push({
	name: 'OS',
	value: ros,
	value_text: '%',
	message: 'Overall survival (R-IPI)<br/>' + os + '% overall survival (IPI)',
});

calc_output.push({
	name: 'PFS',
	value: rpfs,
	value_text: '%',
	message: 'Progression-free survival (R-IPI)<br/>' + pfs + '% progression-free survival (IPI)',
});
