	scr = scr / 88.4;
}

//calculation for CDK-EPI Creatinine
if(calc == 0){
	var u_cr;
	if(gender == 0){			//if female
		if(scr <= 0.7){
			u_cr = 144 * Math.pow(scr/0.7, -0.329) * Math.pow(0.993, age); 
		}else{
			u_cr = 144 * Math.pow(scr/0.7, -1.209) * Math.pow(0.993, age); 
		}
	}else{					//if male
		if (scr <= 0.9){
			u_cr = 141 * Math.pow(scr/0.9, -0.411) * Math.pow(0.993, age); 
		}else{
			u_cr = 141 * Math.pow(scr/0.9, -1.209) * Math.pow(0.993, age); 
		}
	}

	if(race == 1){  //patient is black
		u_cr = u_cr * 1.159;
	} else if(race == 0){  //patient is black
		var u_cr_range = u_cr * 1.159;
      u_cr_range = u_cr_range.toFixed(0);
	} 
	var stage;
	u_cr = u_cr.toFixed(0);
if (u_cr >= 90) {
	stage = 'Stage I';
}
else if (u_cr >= 60 && u_cr < 90) {
	stage = 'Stage II';
}
else if (u_cr >= 30 && u_cr < 60) {
		if (u_cr >= 30 && u_cr < 45){
          stage = 'Stage IIIb';
        } else {
          stage = 'Stage IIIa';
        }
}
else if (u_cr >= 15 && u_cr < 30) {
	stage = 'Stage IV';
}
else {
	stage = 'Stage V';
}

if (race == 0) {
