calc.equation_logic_text:
//outputs
var calc_output = [];

//user inputs
var ecg = parseFloat(ecg);
var ang = parseFloat(ang);
var pnrad = parseFloat(pnrad);
var vomit = parseFloat(vomit);
var sweat = parseFloat(sweat);
var hypo = parseFloat(hypo);

var tnt = parseFloat(tnt);

if (UOMSYSTEM == true) {
	tnt = tnt * 1000
}

//calculation
var u = 1 / (1 + Math.exp(-(1.713*ecg + 0.847*ang + 0.607*pnrad + 1.417*vomit + 2.058*sweat + 1.208*hypo + 0.089*tnt - 4.766)));
u = u.toFixed(2)
risk_acs = u * 100

//results
var risk, msg
if (u < 0.02){
	risk = 'Very low';
	msg = 'ACS ruled out. Consider discharge.';
}else if ( u >= 0.02 && u < 0.05){
	risk = 'Low';
	msg = 'Consider serial troponin in ED ward, e.g. 3h troponin; consider discharge if normal.';
}else if ( u >= 0.05 && u < 0.95){
	risk = 'Moderate';
	msg = 'Serial troponin in general ward, consider stress testing and/or CT coronary angiography.';
}else{
	risk = 'High';
	msg = 'ACS ruled in. Refer to cardiology, treat for ACS.';
}

//result for user
calc_output.push({
	name: 'mini',
	value: risk,
	value_text: 'risk',
	message: risk_acs + '% risk for ACS'
});
calc_output.push({
	name: 'Risk',
	value: risk,
	value_text: 'risk',
	message: msg
});
calc_output.push({
	name: 'Score',
	value: risk_acs.toFixed(0),
	value_text: '%',
	message: 'Risk of ACS or MACE in 30 days'
});
