calc.equation_logic_text:
var calc_output = [];

//user inputs
var predis = parseFloat(predis);
var hx = parseFloat(hx);
var sys = parseFloat(sys);
var trop = parseFloat(trop);
var abn = parseFloat(abn);
var qrs = parseFloat(qrs);
var cqt = parseFloat(cqt);
var syncope = parseFloat(syncope);

//calculation & interpretation
var est;
var risk;

var u = predis + hx + sys + trop + abn + qrs + cqt + syncope;

var assign ={'-3': 0.4, '-2': 0.7, '-1': 1.2, '0': 1.9, '1': 3.1, '2': 5.1, '3': 8.1, '4': 12.9, '5': 19.7, '6': 28.9, '7': 40.3, '8': 52.8, '9': 65.0, '10': 75.5, '11': 83.6};

for(var key in assign){
    if (u == key){
        est = assign[key];
    }
}

if (u == -3 || u == -2){
    risk = 'Very low';
}else if(u == -1 || u == 0){
    risk = 'Low';
}else if(u >= 1 && u <= 3){
    risk = 'Medium';
 }else if(u == 4 || u == 5){
    risk = 'High';
}else if(u >= 6){
    risk = 'Very high';
}

//results
calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: risk + ' risk'
});
calc_output.push({
    name: 'Score',
    value: u,
    value_text: 'points',
    message: 'Canadian Syncope Risk Score'
});
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: ' risk',
    message: est + '% risk of 30-day serious adverse event (death, arrhythmia, MI — full list in Evidence)'
});
