var calc_output = [];


var ped = parseFloat(ped);
var eject = parseFloat(eject);
var vomit = parseFloat(vomit);
var skull = parseFloat(skull);
var gcs = parseFloat(gcs);
var gcsd = parseFloat(gcsd);
var anti = parseFloat(anti);
var seiz = parseFloat(seiz);
var age = parseFloat(age);
var fall = parseFloat(fall);
var per = parseFloat(per);
var amnesia = parseFloat(amnesia);
var neuro = parseFloat(neuro);
var loc = parseFloat(loc);

var u = ped + eject + vomit + skull + gcs + gcsd + anti + seiz + age + fall + per + amnesia + neuro + loc


//interpretation
var risk = 'Low';
var msg = 'CT not necessary.'
var sens = ' 100% sensitive for neurosurgical intervention, 96% sensitive for intracranial traumatic CT findings.'
if(u >= 2){
	risk = 'Not low';
	msg = 'Consider CT.'
    sens = ''
}



//results
calc_output.push({
    name: 'mini',
    value: risk,
    value_text: 'risk',
    message: msg
});
calc_output.push({
    name: 'Score',
    value: risk,
    value_text: 'risk',
    message: msg + sens
});
