calc.equation_logic_text:
var calc_output = [];

//user inputs
var facial = parseFloat(facial);
var arm = parseFloat(arm);
var grip = parseFloat(grip);

//add up points
var u = facial + arm + grip;

var msg, minimsg;
if (u < 4) {
	msg = 'LVO less likely. Stroke is MINOR or MODERATE in severity, but may still benefit from acute treatment.';
    minimsg = 'Minor';
}
if (u >=4) {
    msg = 'LVO likely. Stroke is SEVERE and requires acute treatment. Transfer to stroke center with endovascular capabilities.';
    minimsg = 'Severe, LVO likely';
}

//results
calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: minimsg 
});
calc_output.push({
    name: 'Score',
    value: u,
    value_text: 'points',
    message: msg
});
