var calc_output = [];

//user inputs
var pread = parseFloat(pread);
var cancer = parseFloat(cancer);
var chf = parseFloat(chf);
var afib = parseFloat(afib);
var loc = parseFloat(loc);
var age = parseFloat(age);
var armweak = parseFloat(armweak);
var legweak = parseFloat(legweak);
var neglect = parseFloat(neglect);

//addition of selected points
var display = pread + cancer + chf + afib + loc + Math.floor(age/10) + armweak + legweak + neglect;
var u = Math.ceil(display);

//interpretation - [30day mortality, severe disability, 1year mortality]
var assign = {'5': ['0.7', '0.9', '2.1'], '6': ['1.9', '1.2', '4.8'], '7': ['1.4', '1.3', '4.5'], '8': ['2.1', '2.5', '6.4'], '9': ['4.4', '4.3', '13.1'], '10': ['4.4', '6.0', '16.2'], '11': ['7.6', '9.8', '21.7'], '12': ['10.9', '14.8', '26.3'], '13': ['15.3', '20.3', '32.0'], '14': ['21.7', '30.7', '42.2'], '15': ['29.0', '35.8', '46.0'], '16': ['35.4', '43.9', '57.7'], '17': ['42.5', '54.4', '63.3'], '18': ['50.5', '65.0', '74.3'], '19': ['61.2', '73.2', '73.8'], '20': ['65.9', '78.4', '83.6']};


var arr = ['0', '0', '0'];
for(var key in assign){
	if (u == key){
		arr = assign[key];
	}
	if (u < 6){
		arr = assign[5];
	}
	if (u > 19){
		arr = assign[20];
	}
}

var thirty = arr[0];
var severe = arr[1];
var year = arr[2]



//results
calc_output.push({
    name: 'mini',
    value: display,
    value_text: 'points',
    message: ''
});
calc_output.push({
    name: 'Score',
    value: display,
    value_text: 'points',
    message: 'Severe disability: ' + severe + '%'
});
calc_output.push({
    name: '30 Day',
    value: thirty,
    value_text: '%',
    message: '30 day mortality'
});
calc_output.push({
    name: '1 Year',
    value: year,
    value_text: '%',
    message: '1 year mortality'
});
