calc.equation_logic_text:
var calc_output = [];

//user inputs - addition of selected points
var score = parseFloat(vomit) + parseFloat(pain) + parseFloat(rebound) + parseFloat(body) + parseFloat(poly) + parseFloat(wbc) + parseFloat(crp);

//interpretation
var prob, rec;
if(score < 5){
	prob = 'Low';
	rec = 'Outpatient follow-up (if unaltered general condition)';
}
if(score > 4 && score < 9){
	prob = 'Indeterminate';
	rec = 'In-hospital active observation with serial re-exams, imaging, or diagnostic laparoscopy, according to local practice';
}
if(score > 8 && score < 13){
	prob = 'High';
	rec = 'Surgical exploration recommended';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: prob
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'AIR Score'
});
calc_output.push({
    name: 'interpretation',
    value: prob,
    value_text: 'risk',
    message: rec
});
