calc.equation_logic_text:
var calc_output = [];

//user inputs & formula
var score = parseFloat(stroke) + parseFloat(resp) + parseFloat(heart) + parseFloat(sao) + parseFloat(min) + parseFloat(ecg) + parseFloat(urea) + parseFloat(serum) + parseFloat(troponin) + parseFloat(probnp);

//interpretation 
var results = {
	0: '2.8',
	1: '5.1',
	2: '9.2',
	3: '15.9',
	4: '26.1',
	5: '39.8',
	6: '55.3',
	7: '69.8',
	8: '81.2',
	9: '89.0',
	10: '>89.0',
	11: '>89.0',
	12: '>89.0'
};

//interpretation applied
var result;
for (var key in results){
	if (key == score){
		result = results[key];
	}
}

var risk = 'Low';
var rec = 'Consider discharge with close follow up and return precautions if no other concerning findings';
if(score > 0){
	risk = 'Medium';
	rec = 'May consider discharge based on patient factors';
}
if(score > 2){
	risk = 'High';	
	rec = 'Not safe for discharge by OHFRS'
}
if(score > 4){
	risk = 'Very high';
	rec = 'Not safe for discharge by OHFRS'
}
if(score >9){
	risk = 'Very high';
	rec = 'Not safe for discharge by OHFRS'	
}


//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: ''
});
calc_output.push({
    name: 'percent',
    value: result,
    value_text: '%',
    message: 'Risk of serious adverse event (death, MI, need for ICU/intubation)'
});
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: 'risk',
    message: rec
});
