calc.equation_logic_text:
var calc_output = [];

//user inputs + addition of points
var score = parseFloat(age) + parseFloat(birth) + parseFloat(place) + parseFloat(year);

//interpretation
var pt = 'points';
var msg = 'Abnormal';
if(score === 4){
	msg = 'Normal';
}
if(score === 1){
	pt = 'point';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: pt,
    message: msg + ' cognition'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: pt,
    message: 'AMT-4 Score'
});
calc_output.push({
    name: 'Interpretation',
    value: msg,
    value_text: 'cognition',
    message: ''
});
