var calc_output = [];

//user inputs
var f = parseFloat(f);
var vt = parseFloat(vt) / 1000;

var rsbi = (f/vt).toFixed(0);

var result = 'Likely successful extubation by RSBI.';
var resultsmall = 'Consider all patient factors before extubating (see Pearls/Pitfalls for suggestions). 97% sensitive, original study.';
if(rsbi > 104){
	result = 'Likely to fail extubation by RSBI.';
	resultsmall = 'See Pearls/Pitfalls for limitations of negative RSBI.';
}

//results
calc_output.push({
    name: 'mini',
    value: rsbi,
    value_text: 'breaths/min/L',
    message: result
});
calc_output.push({
    name: 'Result',
    value: rsbi,
    value_text: 'breaths/min/L',
    message: result + '<br/>' + resultsmall
});
