calc.equation_logic_text:
var calc_output = [];

//user inputs
var gender = parseFloat(gender); // 0=female, 1=male
var waist;
if(gender === 0){
	waist = parseFloat(waistW)
}else{
	waist = parseFloat(waistM)
}
var score = parseFloat(age) + waist + parseFloat(bmi) + parseFloat(bpmed) + parseFloat(glucose) + parseFloat(physical) + parseFloat(daily) + parseFloat(famhx);

var pt = 'points';
if(score === 1){pt = 'point'}

//interpretation
var resultM, resultW, risk;
var guide = '';
if(score > -1 && score < 4){
	resultM = '0.3';
	resultW = '0.1';
	risk = 'Very low';
}else if(score > 3 && score < 9){
	resultM = '0.8';
	resultW = '0.4';
	risk = 'Low';
}else if(score > 8 && score < 13){
	resultM = '2.6';
	resultW = '2.2';
	risk = 'Moderate';
	guide = 'Guidelines vary by setting, but consider formal screening with HgbA1c for moderate or greater risk patients, and addressing modifiable risk factors with all patients.';
}else if(score > 12 && score < 21){
	resultM = '23.1';
	resultW = '14.1';
	risk = 'High';
	guide = 'Guidelines vary by setting, but consider formal screening with HgbA1c for moderate or greater risk patients, and addressing modifiable risk factors with all patients.';
}else if(score >= 21){
	resultM = '50';
	resultW = '50';
	risk = 'Very high';
	guide = 'Guidelines vary by setting, but consider formal screening with HgbA1c for moderate or greater risk patients, and addressing modifiable risk factors with all patients.';
}
var final;
if(gender === 0){
	final = resultW;
}else{
	final = resultM;
}

//results
calc_output.push({
    name: 'mini',
    value: final,
    value_text: '%',
    message: '10-year risk of developing T2DM'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: pt,
    message: 'FINDRISC'
});
calc_output.push({
    name: 'Result',
    value: final,
    value_text: '%',
    message: '10-year risk of developing T2DM'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: 'risk',
    message: guide
});
