calc.equation_logic_text:
var calc_output = [];

//user inputs - addition of pts
var score = parseFloat(age) + parseFloat(apache) + parseFloat(sofa) + parseFloat(num) + parseFloat(days) + parseFloat(il);

//interpretation
var extra;
var risk = 'Low';
if(score > 5){
	risk = 'High';
	extra = 'Consider aggressive nutrition therapy (enteral or parenteral).';
}
else {
	extra = ' '
}
var mort;
var dict = {
	'0': 1,
	'1': 2,
	'2': 3,
	'3': 8,
	'4': 11,
	'5': 20,
	'6': 30,
	'7': 45,
	'8': 58,
	'9': 70,
	'10': 80
}

for (var key in dict){
	if (key = score){
		mort = dict[key];
	}
}
if(score === 0){
	mort = 1;
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: 'NUTRIC Score'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'NUTRIC Score'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: 'risk',
    message: '~' + mort + '% mortality at 28 days.<br/>' + extra
});
