var calc_output = [];

//user inputs - addition of points
var score = parseFloat(bun) + parseFloat(age) + parseFloat(pleural) + parseFloat(infection) + parseFloat(albumin);

//interpretation
var risk = "Low";
var mort = "1.5%";
if(score === 3 || score === 4){
	risk = "Medium";
	mort = "17.8%";
}else if(score > 4){
	risk = "High";
	mort = "47.8%";
}

var pt = "points";
if(score === 1){
	pt = "point";
}


//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: pt,
    message: 'RAPID Score'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: pt,
    message: 'RAPID Score'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: "risk",
    message: mort + ' mortality at 3 months'
});
