calc.equation_logic_text:
var calc_output = [];

//user inputs
var score = parseFloat(emrcd) + parseFloat(eos) + parseFloat(cons) + parseFloat(aci) + parseFloat(afib);

var risk = "Low"; 
var rec = "Routine management";
if(score > 1){
	risk = "Intermediate";
	rec = "Use clinician judgment re: disposition";
}
if(score > 2){
	risk = "High";
	rec = "Consider escalation of care vs. palliative care";
}

var pt = 'points';
if(score === 1){
	pt = 'point';
}

var dict = {
	'0': 0,
	'1': 1.5,
	'2': 5.4,
	'3': 15.3,
	'4': 31,
	'5': 40.5,
	'6': 50
}

var mort;

for(var key in dict){
	if(score.toString() === key){
		mort = dict[key];
	}
}


//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: pt,
    message: risk + ' risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: pt,
    message: 'DECAF Score'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: 'risk',
    message: rec + '<br/><br/>' + mort.toString() + '% in-hospital mortality'
});
