calc.equation_logic_text:
var calc_output = [];
var msg ='';
var u = parseInt(glasgow_coma_score) + parseInt(age_80) + parseInt(ich_volume_30) + parseInt(intraventricular_hemorrhage) + parseInt(infratentorial_hemorrhage);
if (u == 0) { msg = '0% mortality.' }
if (u == 1) { msg = '13% mortality.' }
if (u == 2) { msg = '26% mortality.' }
if (u == 3) { msg = '72% mortality.' }
if (u == 4) { msg = '97% mortality.' }
if (u == 5) { msg = '100% mortality.' }
if (u == 6) { msg = 'Although no patients in the studies had scores of 6, the mortality is estimated to be 100%.' }
  msg+='<br/><br/>Please note that the ICH score is primarily used as a clinical grading scale and communication tool.  It is not meant to provide prognostic information, and should not be used as a primary means to predict the outcomes of patients with ICH.';
calc_output.push({ 
  name: 'mini',
  value: u, 
  value_text: 'points', 
  message: 'ICH Score' 
});
calc_output.push({ 
  name: '402_ICH Score_result', 
  value: u, 
  value_text: 'points', 
  message: msg 
});
