calc.equation_logic_text:
var calc_output = [];

var req = parseFloat(req1) + parseFloat(req2) + parseFloat(req3);
var pop = parseFloat(pop);
var history = parseFloat(history);

var carditis = parseFloat(carditis);
var arth1 = parseFloat(arth1); 
var arth2 = parseFloat(arth2); 
var chorea = parseFloat(chorea);
var em = parseFloat(em);
var nodules = parseFloat(nodules);

var prolonged = parseFloat(prolonged);
var arthralgia1 = parseFloat(arthralgia1);
var arthralgia2 = parseFloat(arthralgia2);
var fever1 = parseFloat(fever1);
var fever2 = parseFloat(fever2);
var esr1 = parseFloat(esr1);
var esr2 = parseFloat(esr2);

var msg;
var string;

if (req < 1) {
  msg = 'No evidence of preceding GAS infection; required criteria not met';
  string = '';

} else {

  if (pop == 0) {
    arth = arth1;
    arthralgia = arthralgia1;
    fever = fever1;
    esr = esr1;
  } else {
    arth = arth2;
    arthralgia = arthralgia2;
    fever = fever2;
    esr = esr2;
  }

  var major = carditis + arth + chorea + em + nodules;
  var minor = prolonged + arthralgia + fever + esr;

  if (history == 0) {
    if (major >= 2 || (major >= 1 && minor >= 2)) {

      msg = 'Diagnosis for initial ARF is positive';
      string = 'Positive';

    } else {

      msg = 'Diagnosis for initial ARF is not positive';
      string = 'Not positive';

    }

  } else if (history == 1) {

    if (major >= 2 || (major >= 1 && minor >= 2) || (minor >= 3)) {

      string = 'Positive';
      msg = 'Diagnosis for recurrent ARF is positive';

    } else {
      string = 'Not positive';
      msg = 'Diagnosis for recurrent ARF is not positive';

    }
  }
} 


calc_output.push({ name: 'mini', value: string, value_text: '', message: msg });
calc_output.push({ name: 'Jones ARF', value: string, value_text: '', message: msg });
