calc.equation_logic_text:
var calc_output = [];

if (UOMSYSTEM == true) {
  height = height * 2.54
}

var plt_pre = parseFloat(plt_pre)
var plt_post = parseFloat(plt_post)
var weight = parseFloat(weight)
var height = parseFloat(height)
var bsa = Math.pow(weight * height / 3600, 0.5)
var plt_unit = parseFloat(plt_unit) 
var time = parseFloat(time)

//equations
var cci = Math.round((plt_post - plt_pre) * bsa.toFixed(1) / plt_unit * 1000 / 100)*100

cci = cci.toFixed(0)

var msg

if (time == 0) {
  if (cci > 7500) {
  msg = '1-hr CCI >7,500 indicates successful transfusion (platelet transfusion refractoriness unlikely)'
  }
  else {
    msg = 'Recommend repeat transfusion. Two CCIs ≤7,500 within 1 hour after transfusion suggests refractoriness.'
  }
}
else if (time == 1) {
  if (cci > 4800) {
    msg = '20-hr CCI >4,800 indicates successful transfusion (platelet transfusion refractoriness unlikely)'
  }
  else {
    msg = 'Recommend repeat transfusion. Two CCIs ≤4,800 within 20 hours after transfusion suggests refractoriness.'
  }
} 


//results
if (time == 0) {
  calc_output.push({
      name: 'mini',
      value: cci.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ","),
      value_text: '',
      message: 'Corrected count increment'
  });
  calc_output.push({
      name: 'cci',
      value: cci.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ","),
      value_text: '',
      message: 'Corrected count increment' 
  });
  calc_output.push({
      name: 'message',
      value: '',
      value_text: '',
      message: msg
  });
}
else {
  calc_output.push({
      name: 'mini',
      value: cci.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ","),
      value_text: '',
      message: 'Corrected count increment'
  });
  calc_output.push({
      name: 'cci',
      value: cci.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ","),
      value_text: '',
      message: 'Corrected count increment' 
  });
  calc_output.push({
      name: 'message',
      value: '',
      value_text: '',
      message: msg
  });
}
