calc.equation_logic_text:
var calc_output = [];

// Declare vars

var status = parseFloat(status);
var asa = parseFloat(asa);
var creatinine = parseFloat(creatinine);
var age = parseFloat(age) * 0.0198;
var surgery = parseFloat(surgery);

var b = -5.2529 + status + creatinine + age + surgery + asa;

var risk = Math.exp(b)/(1 + Math.exp(b)) * 100

calc_output.push({
   name: 'mini',
   value: risk.toFixed(1),
   value_text: '%',
   message: 'MICA risk'
});
calc_output.push({
   name: '4038_Gupta Perioperative Risk_result',
   value: risk.toFixed(1),
   value_text: '%',
   message: 'Risk of myocardial infarction or cardiac arrest, intraoperatively or up to 30 days post-op'
});
