calc.equation_logic_text:
var calc_output = [];

// Declare vars

var age = parseFloat(age);
var heartrate = parseFloat(heartrate);
var bloodpressure = parseFloat(bloodpressure);
var temperature = parseFloat(temperature);
var mmhg = parseFloat(mmhg);
var bun = parseFloat(bun);
var urine = parseFloat(urine);
var sodium = parseFloat(sodium);
var potassium = parseFloat(potassium);
var bicarbonate = parseFloat(bicarbonate);
var bilirubin = parseFloat(bilirubin);
var wbc = parseFloat(wbc);
var chronic = parseFloat(chronic);
var admission = parseFloat(admission);
var gcs = parseFloat(gcs);

var score = age + heartrate + bloodpressure + temperature + mmhg + bun + urine + sodium + potassium + bicarbonate + bilirubin + wbc + chronic + admission + gcs;

var x = -7.7631 + (0.0737 * score) + 0.9971 * Math.log(score + 1);
var mortality = Math.exp(x)/(1 + Math.exp(x)) * 100

calc_output.push({
   name: 'mini',
   value: score,
   value_text: 'points',
   message: mortality.toFixed(1) + '% in-hospital mortality'
});
calc_output.push({
   name: 'score',
   value: score,
   value_text: 'points',
   message: 'SAPS II Score'
});
calc_output.push({
   name: 'SAPS risk',
   value: mortality.toFixed(1),
   value_text: '%',
   message: 'In-hospital mortality by SAPS II'
});
