calc.equation_logic_text:
var calc_output = [];
var general, gestation, antibiotics, hyperbilirubinemia, hospitalization, illness, mother, infection, wbc, neutrophils, diarrhea, urinewbc;

var u = parseFloat(general) + parseFloat(gestation) + parseFloat(antibiotics) + parseFloat(antibiotics) + parseFloat(hyperbilirubinemia) + parseFloat(hospitalization) + parseFloat(illness) + parseFloat(mother) + parseFloat(infection) + parseFloat(wbc) + parseFloat(neutrophils) + parseFloat(diarrhea) + parseFloat(urinewbc);

var risk = '';
var recommendation = '';

if (u == 13) {
    risk = 'Low risk';
    recommendation = ' Obtain limited testing (CBC, UA, and blood/urine cultures). Likely safe for discharge home. See Next Steps for details.'
}
else {
    risk = 'Not low risk';
    recommendation = ' Perform further testing: CBC, UA, blood/urine cultures, and likely CSF testing.'
}

calc_output.push({ 
	name: 'mini', 
	value: risk, 
	value_text: '', 
	message: '' 
});
calc_output.push({ 
	name: 'Rochester Criteria', 
	value: risk, 
	value_text: '', 
	message: recommendation 
});
