calc.equation_logic_text:
var calc_output = [];

// Declare vars

var age = parseFloat(age);
var diameter = parseFloat(diameter);
var smoker = parseFloat(smoker);
var cancer = parseFloat(cancer);
var spiculation = parseFloat(spiculation);
var lobe = parseFloat(lobe);
var pet1 = parseFloat(pet1);

var x = (0.0391*age) + (0.1274*diameter) + (0.7917*smoker) + (1.3388*cancer) + (1.0407*spiculation) + (0.7838*lobe) - 6.8272;
var z = Math.exp(x)/(1 + Math.exp(x));
var malignancy = z*100;

var pet, spn;

if (pet1 == 0) {
  pet = malignancy;
  spn = pet;
} else if (pet1 == 1) {
  pet = -4.739 + 3.691*z;
  spn = 100/(1 + Math.exp(-pet));
} else if (pet1 == 2) {
  pet = -4.739 + 3.691*z + 2.322;
  spn = 100/(1 + Math.exp(-pet));
} else if (pet1 == 3) {
  pet = -4.739 + 3.691*z + 4.617;
  spn = 100/(1 + Math.exp(-pet));
} else if (pet1 == 4) {
  pet = -4.739 + 3.691*z + 4.771;
  spn = 100/(1 + Math.exp(-pet));
}


calc_output.push({
   name: 'mini',
   value: spn.toFixed(1),
   value_text: '%',
   message: 'Probability of malignancy'
});
calc_output.push({
   name: 'score',
   value: spn.toFixed(1),
   value_text: '%',
   message: 'Probability of malignancy <br/><br/> One study suggests watchful waiting only at very low post-test probabilities (<2%), biopsy at "lower" post-test probabilities (2% to 20%), and surgery at higher post-test probabilities (>70%). See Next Steps.'
});
