calc.equation_logic_text:
//declare calc_output
var calc_output = [];

//user inputs:
var cr = UOMSYSTEM ? parseFloat(creatinine) : (parseFloat(creatinine) / 88.4)
var weight = parseFloat(weight); // no need to adjust. weight always comes in in SI system
var sex = parseFloat(sex)
var age = parseFloat(age)

//derived variables from user inputs
var sexPts;
var wtCorrection;
if (sex === 0) {
    sexPts = 1;
    wtCorrection = 50;
} else if (sex === 1) {
    sexPts = 0.85;
    wtCorrection = 45.5;
}

//calculate result1
var result1 = (((140 - age) * weight * sexPts) / (72 * cr));

//determine msg1
var msg1 = 'Creatinine clearance, original Cockcroft-Gault'

    calc_output.push({
        name: 'mini',
        value: result1.toFixed(0),
        value_text: 'mL/min',
        message: 'Creatinine clearance'
    });

//if the user did not enter height or the height is 0
if (!parseFloat(height)) {
    calc_output.push({
        name: '43_Creatinine Clearance_result (original)',
        value: result1.toFixed(0),
        value_text: 'mL/min',
        message: msg1
    });
}

//if the user entered height that's not 0
if (parseFloat(height)) {

    var ht = parseFloat(height); // height in cm, BUT OF COURSE WE NEED FREAKING INCHES
    var ht_inches = ht / 2.54; // height in inches
    var ibw = (ht_inches <= 60) ? wtCorrection : (wtCorrection + 2.3 * (ht_inches - 60))
    var ibw_crcl = ((140 - age) * ibw * sexPts) / (72 * cr); // if pt was ideal body weight, this would be their CrCl. used below.
    var bmi = weight / Math.pow(ht / 100, 2); // weight already in kg, ht in cm so divide by 100 to get ht in meters for correct bmi formula

    switch (true) {

        case (bmi > 0 && bmi < 18.5):
            calc_output.push({ // add the modified CrCl for weight here
                name: '43_Creatinine Clearance_result (modified)',
                value: result1.toFixed(0),
                value_text: 'mL/min',
                message: 'Creatinine clearance for underweight patient (BMI ' + bmi.toFixed(1) + ' kg/m²), calculated using actual body weight (no adjustment).'
            })
            break

        case (bmi >= 18.5 && bmi < 25): // intermed BMI so check wt vs IBW

            calc_output.push({
                name: '43_Creatinine Clearance_result (original)',
                value: result1.toFixed(0),
                value_text: 'mL/min',
                message: msg1
            });

            var value_corrected = (140 - age) * ibw * sexPts / (72 * cr);

            var msgAppend = '';
            if (weight.toFixed(0) == ibw.toFixed(0)) {
                msgAppend = 'normal weight patient. Note: This patient\'s IBW is equal to their actual weight, so this is likely near their true CrCl and no adjustment is made.';
            } else {
                msgAppend = 'normal weight patient, using ideal body weight of ' + ibw.toFixed(0) + ' kg (' + (ibw.toFixed(0) * 2.2).toFixed(0) + ' lbs).';
            }

            calc_output.push({ // add the modified CrCl for weight here
                name: '43_Creatinine Clearance_result (modified)',
                value: value_corrected.toFixed(0),
                value_text: 'mL/min',
                message: 'Creatinine clearance for ' + msgAppend
            });

            if (weight !== ibw) {
                var range;
                if (weight > ibw) {
                    range = ibw_crcl.toFixed(1) + '-' + result1.toFixed(1);
                } else {
                    range = result1.toFixed(1) + '-' + ibw_crcl.toFixed(1);
                }
                calc_output.push({
                    name: '43_Creatinine Clearance_result (range)',
                    value: range,
                    value_text: 'mL/min',
                    message: "Note: This range uses IBW and actual body weight. Controversy exists over which form of weight to use."
                });
            }
            break

        case (bmi >= 25):

            calc_output.push({
                name: '43_Creatinine Clearance_result (original)',
                value: result1.toFixed(0),
                value_text: 'mL/min',
                message: msg1
            });

            var abw = ibw + 0.4 * (weight - ibw); // adjusted body weight for obesity
            var abw_lbs = abw * 2.2
            var value_corrected = (140 - age) * abw * sexPts / (72 * cr);

            calc_output.push({ // add the modified CrCl for weight here
                name: '43_Creatinine Clearance_result (modified)',
                value: value_corrected.toFixed(0),
                value_text: 'mL/min',
                message: 'Creatinine clearance modified for overweight patient, using adjusted body weight of ' + abw.toFixed(0) + ' kg (' + abw_lbs.toFixed(0) + ' lbs).'
            });

            if (ibw_crcl !== value_corrected) {
            var range;
                if (ibw_crcl < value_corrected) {
                    range = ibw_crcl.toFixed(1) + '-' + value_corrected.toFixed(1);
                } else {
                    range = value_corrected.toFixed(1) + '-' + ibw_crcl.toFixed(1);
                }
            }

            calc_output.push({
                name: '43_Creatinine Clearance_result (range)',
                value: range,
                value_text: 'mL/min',
                message: "Note: This range uses IBW and adjusted body weight. Controversy exists over which form of weight to use."
            });
            break
        default:
            break
    }
}
