calc.equation_logic_text:
var calc_output = [];
var pt_ratio = parseFloat(pt) / parseFloat(pt_lab_normal);
var bili = parseFloat(bilirubin);
var bun = parseFloat(bun);
var age = parseFloat(age);
var age_score, wbc_score, bun_score, pt_score, bili_score;
var output_score = 0;
var output_msg = '';
var white_blood_cell_count = parseFloat(white_blood_cell_count);
if (UOMSYSTEM == false) {
    bili = bili;
    bun = bun;
    //all others are 1:1 conversions, need SI units for this corresponding scores
}
if (UOMSYSTEM == true) {
    bili = bili * 17.104;
    bun = bun * 0.357;
    //all others are 1:1 conversions, need SI units for this corresponding scores
}

if (age < 50) { age_score = 1; } else { age_score = 2; }
if (white_blood_cell_count < 15) { wbc_score = 1; } else { wbc_score = 2; }
if (bun < 5) { bun_score = 1; } else { bun_score = 2; }
if (pt_ratio > 2) { pt_score = 3; }
if (pt_ratio > 1.499 && pt_ratio < 2.001) { pt_score = 2; }
if (pt_ratio < 1.5) { pt_score = 1; }
if (bili > 250) { bili_score = 3; }
if (bili > 124.99 && bili < 250.001) { bili_score = 2; }
if (bili < 125) { bili_score = 1; }
output_score = age_score + wbc_score + bun_score + pt_score + bili_score;
if (output_score < 9) { output_msg = 'Scores of less than 9 portended a much better 28- and 84-day survival than scores above 9 or above (87%/79% vs. 46%/40%). The score was less sensitive but more accurate than the Modified Discriminant Function or MELD scores.'; } else { output_msg = 'Scores of greater than or equal to 9 portended a much poorer 28- and 84-day survival than scores less than 9 (46%/40% vs. 87%/79%). The score was less sensitive but more accurate than the Modified Discriminant Function or MELD scores.'; }
calc_output.push({ name: 'mini', value: output_score, value_text: 'points', message: 'Glasgow Alcoholic Hepatitis Score' });
calc_output.push({ name: 'Glasgow Alcoholic Hepatitis Score', value: output_score, value_text: 'points', message: output_msg });
