var calc_output = [];
var u = parseFloat(wbc_16k) + parseFloat(age_55) + parseFloat(glucose_200) + parseFloat(ast_250) + parseFloat(ldh_350);
var v = u + parseFloat(hct_10) + parseFloat(bun_5) + parseFloat(ca_8) + parseFloat(po2_60) + parseFloat(base_deficit_4) + parseFloat(fluid_needs_6);
var msg = '';
var txt = '';
if (u < 3) { txt = 'Severe pancreatitis unlikely.'; }
if (u == 3 || u == 4) { txt = 'Severe pancreatitis likely. Consider ICU admission.'; }
if (u == 5 || u == 6) { txt = 'Severe pancreatitis likely. Consider ICU admission.'; }
if (u > 6) { txt = 'Severe pancreatitis likely. Consider ICU admission.'; }
if (v < 3) { msg = '1% predicted mortality.'; }
if (v == 3 || v == 4) { msg = '15% predicted mortality.'; }
if (v == 5 || v == 6) { msg = '40% predicted mortality.' }
if (v > 6) { msg = '100% predicted mortality.'; }

calc_output.push({ 
    name: 'mini', 
    value: v.toFixed(0), 
    value_text: 'points', 
    message: msg 
});
calc_output.push({
    name: "89_Ranson's Criteria_result (admission)", 
    value: u.toFixed(0), 
    value_text: 'points', 
    message: "Ranson's Criteria (On Admission)<br/><br/>" + txt 
});
calc_output.push({ 
    name: "89_Ranson's Criteria_result (cumulative)", 
    value: v.toFixed(0), 
    value_text: 'points', 
    message: "Ranson's Criteria (Cumulative)<br/><br/>" + msg 
});
